import UIKit

struct Clause {
    var clauseNumber: Int!
    var clause : String!
}

class DetailsViewController: UIViewController {
    
    @IBOutlet weak var scrollView: UIScrollView!
    @IBOutlet weak var Label1: UILabel!
    @IBOutlet weak var Label2: UILabel!
    
    var Clauses = [Clause]()
    
    var chechNumber = Int()
    
    override func viewDidLoad() {
        super.viewDidLoad()

        // Do any additional setup after loading the view.
    scrollView.contentLayoutGuide.bottomAnchor.constraint(equalTo: Label2.bottomAnchor).isActive = true
        
        //Clause(clauseNumber:, clause: ""),
        
        Clauses = [Clause(clauseNumber:0, clause: "Bu Sözleşmeye Taraf Devletler: Birleşmiş Milletler Andlaşmasında ilan edilen ilkeler uyarınca insanlık ailesinin tüm üyelerinin, doğuştan varlıklarına özgü bulunan haysiyetle birlikte eşit ve devredilemez haklara sahip olmalarının tanınmasının, dünyada özgürlük, adalet ve barışın temeli olduğunu düşünerek, Birleşmiş Milletler halklarının, insanın temel haklarına ve bireyin, insan olarak taşıdığı haysiyet ve değere olan kesin inançlarını Birleşmiş Milletler Andlaşmasında bir kez daha doğrulamış olduklarını ve daha geniş bir özgürlük ortamında toplumsal ilerleme ve daha iyi bir yaşam düzeyi sağlama yolundaki kararlılıklarını hatırda tutarak, Birleşmiş Milletlerin, İnsan Hakları Evrensel Bildirisinde ve Uluslararası İnsan Hakları Sözleşmelerinde herkesin, bu metinlerde yer alan hak ve özgürlüklerden ırk, renk, cinsiyet, dil, din, siyasal ya da başka görüş, ulusal ya da toplumsal köken, mülkiyet, doğuştan veya başka durumdan kaynaklanan ayırımlar dahil, hiçbir ayırım gözetilmeksizin yararlanma hakkına sahip olduklarını benimsediklerini ve ilan ettiklerini kabul ederek, Uluslararası İnsan Hakları Evrensel Bildirisinde, Birleşmiş Milletlerin, çocukların özel ilgi ve yardıma hakkı olduğunu ilan ettiğini anımsayarak, Toplumun temel birimi olan ve tüm üyelerinin ve özellikle çocukların gelişmeleri ve esenlikleri için doğal ortamı oluşturan ailenin toplum içinde kendisinden beklenen sorumlulukları tam olarak yerine getirebilmesi için gerekli koruma ve yardımı görmesinin zorunluluğuna inanmış olarak, Çocuğun kişiliğinin tam ve uyumlu olarak gelişebilmesi için mutluluk, sevgi ve anlayış havasının içindeki bir aile ortamında yetişmesinin gerekliliğini kabul ederek, Çocuğun toplumda bireysel bir yaşantı sürdürebilmesi için her yönüyle hazırlanmasının ve Birleşmiş Milletler Andlaşmasında ilan edilen ülküler ve özellikle barış, değerbilirlik, hoşgörü, özgürlük, eşitlik ve dayanışma ruhuyla yetiştirilmesinin gerekliliğini göz önünde bulundurarak, Çocuğa özel bir ilgi gösterme gerekliliğinin, 1924 tarihli, Cenevre Çocuk Hakları Bildirisinde ve 20 Kasım 1959 tarihinde Birleşmiş Milletler Teşkilatı Genel Kurulunca kabul edilen Çocuk Hakları Bildirisinde belirtildiğini ve İnsan Hakları Evrensel Bildirisinde, Medeni ve Siyasi Haklar Uluslararası Sözleşmesinde (özellikle 23 ve 24 üncü Maddelerinde) ve Ekonomik, Sosyal ve Kültürel Haklara İlişkin Uluslararası Sözleşmede (özellikle 10 uncu Maddesinde) ve çocukların esenliği ile ilgili uzman kuruluşların ve uluslararası örgütlerin kurucu ve ilgili belgelerinde tanındığını hatırda tutarak, Çocuk Hakları Bildirisinde de belirtildiği gibi, 'çocuğun gerek bedensel gerek zihinsel bakımdan tam erginliğe ulaşmamış olması nedeniyle doğum sonrasında olduğu kadar, doğum öncesinde de uygun yasal korumayı da içeren özel güvence ve koruma gereksiniminin bulunduğu'nu hatırda tutarak, Ulusal ve uluslararası düzeyde çocukları aile yanına yerleştirme ve evlat edinmeye de özel atıfta bulunan Çocuğun Korunması ve Esenliğine İlişkin Toplumsal ve Hukuksal İlkeler Bildirisi; Çocuk Mahkemelerinin Yönetimi Hakkında Birleşmiş Milletler Asgari Standart Kuralları (Pekin Kuralları) ve Acil Durumlarda ve Silahlı Çatışma Halinde Kadınların ve Çocukların Korunmasına İlişkin Bildirinin hükümlerini anımsayarak, Dünyadaki ülkelerin tümünde çok güç koşullar altında yaşayan ve bu nedenle özel bir ilgiye gereksinimi olan çocukların bulunduğu bilinci içinde, Çocuğun korunması ve uyumlu gelişmesi bakımından her halkın kendine özgü geleneklerinin ve kültürel değerlerinin taşıdığı önemi gözönünde tutarak, Her ülkedeki, özellikle gelişmekte olan ülkelerdeki çocukların yaşama koşullarının iyileştirilmesi için uluslararası işbirliğinin taşıdığı önemin bilincinde olarak, Aşağıdaki kurallar üzerinde anlaşmaya varmışlardır"),
                   Clause(clauseNumber:1, clause: "Bu Sözleşme uyarınca çocuğa uygulanabilecek olan kanuna göre daha erken yaşta reşit olma durumu hariç, onsekiz yaşına kadar her insan çocuk sayılır."),
                   Clause(clauseNumber:2, clause: "1. Taraf Devletler, bu Sözleşmede yazılı olan hakları kendi yetkileri altında bulunan her çocuğa, kendilerinin, ana babalarının veya yasal vasilerinin sahip oldukları, ırk, renk, cinsiyet, dil, siyasal ya da başka düşünceler, ulusal, etnik ve sosyal köken, mülkiyet, sakatlık, doğuş ve diğer statüler nedeniyle hiçbir ayrım gözetmeksizin tanır ve taahhüt ederler. 2. Taraf Devletler, çocuğun ana-babasının, yasal vasilerinin veya ailesinin öteki üyelerinin durumları, faaliyetleri, açıklanan düşünceleri veya inançları nedeniyle her türlü ayırıma veya cezaya tabi tutulmasına karşı etkili biçimde korunması için gerekli tüm uygun önlemi alırlar." ),
                   Clause(clauseNumber:3, clause: "1. Kamusal ya da özel sosyal yardım kuruluşları, mahkemeler, idari makamlar veya yasama organları tarafından yapılan ve çocukları ilgilendiren bütün faaliyetlerde, çocuğun yararı temel düşüncedir. 2. Taraf Devletler, çocuğun ana-babasının, vasilerinin ya da kendisinden hukuken sorumlu olan diğer kişilerin hak ve ödevlerini de göz önünde tutarak, esenliği için gerekli bakım ve korumayı sağlamayı üstlenirler ve bu amaçla tüm uygun yasal ve idari önlemleri alırlar. 3. Taraf Devletler, çocukların bakımı veya korunmasından sorumlu kurumların; hizmet ve faaliyetlerin özellikle güvenlik, sağlık, personel sayısı ve uygunluğu ve yönetimin yeterliliği açısından, yetkili makamlarca konulan ölçülere uymalarını taahhüt ederler."),
                   Clause(clauseNumber:4, clause: "Taraf Devletler, bu Sözleşmede tanınan hakların uygulanması amacıyla gereken her türlü yasal, idari ve diğer önlemleri alırlar. Ekonomik, sosyal ve kültürel haklara ilişkin olarak, Taraf Devletler eldeki kaynaklarını olabildiğince geniş tutarak, gerekirse uluslararası işbirliği çerçevesinde bu tür önlemler alırlar."),
                   Clause(clauseNumber:5, clause: "T araf Devletler, bu Sözleşmenin çocuğa tanıdığı haklar doğrultusunda çocuğun yeteneklerinin geliştirilmesi ile uyumlu olarak, çocuğa yol gösterme ve onu yönlendirme konusunda ana-babanın, yerel gelenekler öngörüyorsa uzak aile veya topluluk üyelerinin, yasal vasilerinin veya çocuktan hukuken sorumlu öteki kişilerin sorumluluklarına, haklarına ve ödevlerine saygı gösterirler."),
                   Clause(clauseNumber:6, clause: "1. Taraf Devletler, her çocuğun temel yaşama hakkına sahip olduğunu kabul ederler. 2. Taraf Devletler, çocuğun hayatta kalması ve gelişmesi için mümkün olan azami çabayı gösterirler."),
                   Clause(clauseNumber:7, clause: "1. Çocuk doğumdan hemen sonra derhal nüfus kütüğüne kaydedilecek ve doğumdan itibaren bir isim hakkına, bir vatandaşlık kazanma hakkına ve mümkün olduğu ölçüde ana- babasını bilme ve onlar tarafından bakılma hakkına sahip olacaktır. 2. Taraf Devletler, özellikle çocuğun tabiiyetsiz kalması söz konusu olduğunda kendi ulusal hukuklarına ve ilgili uluslararası belgeler çerçevesinde üstlendikleri yükümlülüklerine uygun olarak bu hakların işlerlik kazanmasını taahhüt ederler."),
                   Clause(clauseNumber:8, clause: "1. Taraf Devletler, yasanın tanıdığı şekliyle çocuğun kimliğini; tabiiyeti, ismi ve aile bağları dahil, koruma hakkına saygı göstermeyi ve bu konuda yasa dışı müdahalelerde bulunmamayı taahhüt ederler. 2. Çocuğun kimliğinin unsurlarının bazılarından veya tümünden yasaya aykırı olarak yoksun bırakılması halinde, Taraf Devletler çocuğun kimliğine süratle yeniden kavuşturulması amacıyla gerekli yardım ve korumada bulunurlar."),
                   Clause(clauseNumber:9, clause: "1. Yetkili makamlar uygulanabilir yasa ve usullere göre ve temyiz yolu açık olarak, ayrılığın çocuğun yüksek yararına olduğu yolunda karar vermedikçe, Taraf Devletler, çocuğun ana- babasından, onların rızası dışında ayrılmamasını güvence altına alırlar. Ancak, ana-babası tarafından çocuğun kötü muameleye maruz bırakılması ya da ihmal edilmesi durumlarında ya da ana-babanın birbirinden ayrı yaşaması nedeniyle çocuğun ikametgahının belirlenmesi amacıyla karara varılması gerektiğinde, bu tür bir ayrılık kararı verilebilir. 2. Bu Maddenin birinci fıkrası uyarınca girişilen her işlemde, ilgili bütün taraflara işleme katılma ve görüşlerini bildirme olanağı tanınır. 3. Taraf Devletler, ana-babasından veya bunlardan birinden ayrılmasına karar verilen çocuğun, kendi yüksek yararına aykırı olmadıkça, ana-babanın ikisiyle de düzenli bir biçimde kişisel ilişki kurma ve doğrudan görüşme hakkına saygı gösterirler. 4. Böyle bir ayrılık, bir Taraf Devlet tarafından girişilen ve çocuğun kendisinin ana veya babasının veya her ikisinin birden tutuklanmasını, hapsini, sürgün, sınır dışı edilmesini veya ölümünü (ki buna devletin gözetimi altında iken nedeni ne olursa olsun meydana gelen ölüm dahildir) tevlit eden herhangi benzer bir işlem sonucu olmuşsa, bu Taraf Devlet, istek üzerine ve çocuğun esenliğine zarar vermemek koşulu ile; ana-babaya, çocuğa veya uygun olursa, ailenin bir başka üyesine, söz konusu aile bireyinin ya da bireylerinin bulunduğu yer hakkında gereken bilgiyi verecektir. T araf Devletler, böyle bir istemin başlı başına sunulmasının ilgili kişi veya kişiler bakımından aleyhe hiç bir sonuç yaratmamasını ayrıca taahhüt ederler."),
                   Clause(clauseNumber:10, clause: "1. 9 uncu Maddenin 1 inci fıkrası uyarınca Taraf Devletlere düşen sorumluluğa uygun olarak, çocuk veya ana-babası tarafından, ailenin birleşmesi amaçlarıyla yapılan bir Taraf Devlet ülkesine girme ya da onu terk etme konusundaki her başvuru, Taraf Devletlerce olumlu, insani ve ivedi bir tutumla ele alınacaktır. Taraf Devletler, bu tür bir başvuru yapılmasının başvuru sahipleri veya aile üyeleri aleyhine sonuçlar yaratmamasını taahhüt ederler. 2. Ana-babası, ayrı devletlerde oturan bir çocuk olağanüstü durumlar hariç, hem ana hem de babası ile düzenli biçimde kişisel ilişkiler kurma ve doğrudan görüşme hakkına sahiptir. Bu nedenle ve 9 uncu Maddenin 1 inci fıkrasına göre Taraf Devletlere düşen sorumluluğa uygun olarak, Taraf Devletler çocuğun ve ana-babasının Taraf Devletlerinin ülkeleri dahil herhangi bir ülkeyi terketme ve kendi ülkelerine dönme hakkına saygı gösterirler. Herhangi bir ülkeyi terketme hakkı, yalnızca yasada öngörüldüğü gibi ve ulusal güvenliği, kamu düzenini, kamu sağlığı ve ahlak veya başkalarının hak ve özgürlüklerini korumak amacı ile ve işbu Sözleşme ile tanınan öteki haklarla bağdaştığı ölçüde kısıtlamalara konu olabilir."),
                   Clause(clauseNumber:11, clause: "1. Taraf Devletler, çocukların yasa dışı yollarla ülke dışına çıkarılıp geri döndürülmemesi halleriyle mücadele için önlemler alırlar. 2. Bu amaçla Taraf Devletler iki ya da çok taraflı anlaşmalar yapılmasını ya da mevcut anlaşmalara katılmayı teşvik ederler."),
                   Clause(clauseNumber:12, clause: "1. Taraf Devletler, görüşlerini oluşturma yeteneğine sahip çocuğun kendini ilgilendiren her konuda görüşlerini serbestçe ifade etme hakkını bu görüşlere çocuğun yaşı ve olgunluk derecesine uygun olarak, gereken özen gösterilmek suretiyle tanırlar. 2. Bu amaçla, çocuğu etkileyen herhangi bir adli veya idari kovuşturmada çocuğun ya doğrudan doğruya veya bir temsilci ya da uygun bir makam yoluyla dinlenilmesi fırsatı, ulusal yasanın usule ilişkin kurallarına uygun olarak çocuğa, özellikle sağlanacaktır."),
                   Clause(clauseNumber:13, clause: "1. Çocuk, düşüncesini özgürce açıklama hakkına sahiptir; bu hak ülke sınırları ile bağlı olmaksızın; yazılı, sözlü, basılı, sanatsal biçimde veya çocuğun seçeceği başka bir araçla her türlü haber ve düşüncelerin araştırılması, elde edilmesi ve verilmesi özgürlüğünü içerir. 2. Bu hakkın kullanılması yalnızca: a) Başkasının haklarına ve itibarına saygı, b) Milli güvenliğin, kamu düzeninin, kamu sağlığı ve ahlakın korunması nedenleriyle ve kanun tarafından öngörülmek ve gerekli olmak kaydıyla yapılan sınırlamalara konu olabilir."),
                   Clause(clauseNumber:14, clause: "1. Taraf Devletler, çocuğun düşünce, vicdan ve din özgürlükleri  2. Taraf Devletler, ana-babanın ve gerekiyorsa yasal vasilerin; çocuğun yeteneklerinin gelişmesiyle bağdaşır biçimde haklarının kullanılmasında çocuğa yol gösterme konusundaki hak ve ödevlerine, saygı gösterirler. 3. Bir kimsenin dinini ve inançlarını açıklama özgürlüğü kanunla öngörülmek ve gerekli olmak kaydıyla yalnızca kamu güvenliği, düzeni, sağlık ya da ahlaki ya da başkalarının temel hakları ve özgürlüklerini korumak gibi amaçlarla sınırlandırılabilir."),
                   Clause(clauseNumber:15, clause: "1. Taraf Devletler, çocuğun dernek kurma ve barış içinde toplanma özgürlüklerine ilişkin haklarını kabul ederler. 2. Bu hakların kullanılması, ancak yasayla zorunlu kılınan ve demokratik bir toplumda gerekli olan ulusal güvenlik, kamu güvenliği, kamu düzeni yararına olarak ya da kamu sağlığı ve ahlakın ya da başkalarının hak ve özgürlüklerinin korunması amaçlarıyla yapılan sınırlamalardan başkalarıyla kısıtlandırılamaz."),
                   Clause(clauseNumber:16, clause: "1. Hiçbir çocuğun özel yaşantısına aile, konut ve iletişimine keyfi ya da haksız bir biçimde müdahale yapılamayacağı gibi, onur ve itibarına da haksız olarak saldırılamaz. 2. Çocuğun bu tür müdahale ve saldırılara karşı yasa tarafından korunmaya hakkı vardır."),
                   Clause(clauseNumber:17, clause: "Taraf Devletler, kitle iletişim araçlarının önemini kabul ederek çocuğun; özellikle toplumsal, ruhsal ve ahlaki esenliği ile bedensel ve zihinsel sağlığını geliştirmeye yönelik çeşitli ulusal ve uluslararası kaynaklardan bilgi ve belge edinmesini sağlarlar. Bu amaçla Taraf Devletler: a) Kitle iletişim araçlarını çocuk bakımından toplumsal ve kültürel yararı olan ve 29 uncu maddenin ruhuna uygun bilgi ve belgeyi yaymak için teşvik ederler; b) Çeşitli kültürel, ulusal ve uluslararası kaynaklardan gelen bu türde bilgi ve belgelerin üretimi, değişimi ve yayımı amacıyla uluslararası işbirliğini teşvik ederler; c) Çocuk kitaplarının üretimini ve yayılmasını teşvik ederler; d) Kitle iletişim araçlarını azınlık grubu veya bir yerli ahaliye mensup çocukların dil gereksinimlerine özel önem göstermeleri konusunda teşvik ederler; e) 13 ve 18 inci maddelerde yer alan kurallar göz önünde tutularak çocuğun esenliğine zarar verebilecek bilgi ve belgelere karşı korunması için uygun yönlendirici ilkeler geliştirilmesini teşvik ederler."),
                   Clause(clauseNumber:18, clause: "1. Taraf Devletler, çocuğun yetiştirilmesinde ve gelişmesinin sağlanmasında ana-babanın birlikte sorumluluk taşıdıkları ilkesinin tanınması için her türlü çabayı gösterirler. Çocuğun yetiştirilmesi ve geliştirilmesi sorumluluğu ilk önce ana babaya ya da durum gerektiriyorsa yasal vasilere düşer. Bu kişiler her şeyden önce çocuğun yüksek yararını göz önünde tutarak hareket ederler. 2. Bu Sözleşmede belirtilen hakların güvence altına alınması ve geliştirilmesi için Taraf Devletler, çocuğun yetiştirilmesi konusundaki sorumluluklarını kullanmada ana-baba ve yasal vasilerin durumlarına uygun yardım yapar ve çocukların bakımı ile görevli kuruluşların, faaliyetlerin ve hizmetlerin gelişmesini sağlarlar. 3. Taraf Devletler, çalışan ana-babanın, çocuk bakım hizmet ve tesislerinden, çocuklarının da bu hizmet ve tesislerden yararlanma hakkını sağlamak için uygun olan her türlü önlemi alırlar."),
                   Clause(clauseNumber:19, clause: "1. Bu Sözleşmeye Taraf Devletler, çocuğun ana-babasının ya da onlardan yalnızca birinin, yasal vasi veya vasilerinin ya da bakımını üstlenen herhangi bir kişinin yanında iken bedensel veya zihinsel saldırı, şiddet veya suistimale, ihmal ya da ihmalkar muameleye, ırza geçme dahil her türlü istismar ve kötü muameleye karşı korunması için; yasal, idari, toplumsal, eğitsel bütün önlemleri alırlar. 2. Bu tür koruyucu önlemler; burada tanımlanmış olan çocuklara kötü muamele olaylarının önlenmesi, belirlenmesi, bildirilmesi, yetkili makama havale edilmesi, soruşturulması, tedavisi ve izlenmesi için gerekli başkaca yöntemleri ve uygun olduğu takdirde adliyenin işe el koyması olduğu kadar durumun gereklerine göre çocuğa ve onun bakımını üstlenen kişilere, gereken desteği sağlamak amacı ile sosyal programların düzenlenmesi için etkin usulleri de içermelidir."),
                   Clause(clauseNumber:20, clause: "1. Geçici ve sürekli olarak aile çevresinden yoksun kalan veya kendi yararına olarak bu ortamda bırakılması kabul edilmeyen her çocuk, Devletten özel koruma ve yardım görme hakkına sahip olacaktır. 2. Taraf Devletler bu durumdaki bir çocuk için kendi ulusal yasalarına göre, uygun olan bakımı sağlayacaklardır. 3. Bu tür bakım, başkaca benzerleri yanında, bakıcı aile yanına verme, İslam Hukukunda kefalet (kafalah), evlat edinme ya da gerekiyorsa çocuk bakımı amacı güden uygun kuruluşlara yerleştirmeyi de içerir. Çözümler düşünülürken, çocuğun yetiştirilmesinde sürekliliğin korunmasına ve çocuğun etnik, dinsel, kültürel ve dil kimliğine gereken saygı gösterilecektir."),
                   Clause(clauseNumber:21, clause: "Evlat edinme sistemini kabul eden ve/veya buna izin veren Taraf Devletler, çocuğun en yüksek yararlarının temel düşünce olduğunu kabul edecek ve aşağıdaki ilkeleri gerçekleştireceklerdir: a) Bir çocuğun evlat edinilmesine ancak yetkili makam karar verir. Bu makam uygulanabilir yasa ve usullere göre ve güvenilir tüm bilgilerin ışığında; çocuğun, ana-babası, yakınları ve yasal vasisine göre durumunu göz önüne alarak ve gereken durumlarda tüm ilgilerlilerle yapılacak görüşme sonucu onların da evlat edinme konusundaki onaylarını alma zorunluluğuna uyarak, kararını verir. b) Çocuğun kendi ülkesinde elverişli biçimde bakılması mümkün olmadığı veya evlat edinecek veya yanına yerleştirilecek aile bulunmadığı takdirde, ülkelerarası evlat edinmenin çocuk bakımından uygun bir çözüm olduğunu kabul ederler. c) Başka bir ülkede evlat edinilmesi düşünülen çocuğun, kendi ülkesinde mevcut evlat edinme durumuyla eşdeğer olan güvence ve ölçülerden yararlanmasını sağlarlar. d) Ülkelerarası evlat edinmede, yerleştirmenin ilgililer bakımından yasadışı para kazanma konusu olmaması için gereken bütün önlemleri alırlar. e) Bu maddedeki amaçları, uygun olduğu ölçüde, ikili ya da çok taraflı düzenleme veya anlaşmalarla teşvik ederler ve bu çerçevede, çocuğun başka bir ülkede yerleştirilmesinin yetkili makam veya organlar tarafından yürütülmesini güvenceye almak için çaba gösterirler."),
                   Clause(clauseNumber:22, clause: "1. Taraf Devletler, ister tek başına olsun isterse ana babası veya herhangi bir başka kimse ile birlikte bulunsun, mülteci statüsü kazanmaya çalışan ya da uluslararası veya iç hukuk kural ve usulleri uyarınca mülteci sayılan bir çocuğun, bu Sözleşmede ve insan haklarına veya insani konulara ilişkin ve söz konusu Devletlerin taraf oldukları diğer Uluslararası sözleşmelerde tanınan ve bu duruma uygulanabilir nitelikte bulunan hakları kullanması amacıyla koruma ve insani yardımdan yararlanması için gerekli bütün önlemleri alırlar. 2. Bu nedenle, Taraf Devletler, uygun gördükleri ölçüde, Birleşmiş Milletler Teşkilatı ve onunla işbirliği yapan hükümetlerarası ve hükümet dışı yetkili başka kuruluşlarla bu durumda olan bir çocuğu korumak, ona yardım etmek, herhangi bir mülteci çocuğun ailesi ile yeniden bir araya gelebilmesi için ana-babası veya ailesinin başka üyeleri hakkında bilgi toplamak amacıyla işbirliğinde bulunurlar. Herhangi bir nedenle kendi aile çevresinden sürekli ya da geçici olarak ayrı düşmüş bir çocuğa bu Sözleşmeye göre tanınan koruma, aynı esaslar içinde, ana-babası ya da ailesinin başkaca üyelerinden hiçbirisi bulunamayan çocuğa da tanınacaktır."),
                   Clause(clauseNumber:23, clause: "1. Taraf devletler, zihinsel ya da bedensel özürlü çocukların saygınlıklarını güvence altına alan, özgüvenlerini geliştiren ve toplumsal yaşama etkin biçimde katılmalarını kolaylaştıran şartlar altında eksiksiz bir yaşama sahip olmalarını kabul ederler. 2. Taraf Devletler, özürlü çocukların özel bakımdan yararlanma hakkını tanırlar ve eldeki kaynakların yeterliliği ölçüsünde ve yapılan başvuru üzerine, yardımdan yararlanabilecek durumda olan çocuğa ve onun bakımından sorumlu olanlara, çocuğun durumu ve ana- babanın veya çocuğa bakanların içinde bulundukları koşullara uygun düşecek yardımın yapılmasını teşvik ve taahhüt ederler. 3. Özürlü çocuğun, özel bakıma gereksinimi olduğu bilincinden hareketle bu maddenin 2 nci fıkrası uyarınca yapılması öngörülen yardım, çocuğun ana-babasının ya da çocuğa bakanların parasal (mali) durumları göz önüne alınarak, olanaklar ölçüsünde ücretsiz sağlanır. Bu yardım; özürlü çocuğun eğitimi, meslek eğitimi, tıbbi bakım hizmetleri, rehabilitasyon hizmetleri, bir işte çalışabilecek duruma getirme hazırlık programları ve dinlenme/eğlenme olanaklarından etkin olarak yararlanmasını sağlamak üzere düzenlenir ve çocuğun en eksiksiz biçimde toplumla bütünleşmesi yanında, kültürel ve ruhsal yönü dahil bireysel gelişmesini gerçekleştirme amacını güder. 4. Taraf Devletler, uluslararası işbirliği ruhu içinde, özürlü çocukların koruyucu sıhhi bakımı, tıbbi, psikolojik ve işlevsel tedavileri alanlarına ilişkin gerekli bilgilerin alışverişi yanında, rehabilitasyon, eğitim ve mesleki eğitim hizmetlerine ilişkin yöntemlerin bilgilerini de içerecek şekilde ve T araf Devletlerin bu alanlardaki güçlerini, anlayışlarını geliştirmek ve deneyimlerini zenginleştirmek amacıyla bilgi dağıtımını ve bu bilgiden yararlanmayı teşvik ederler. Bu bakımdan, gelişmekte olan ülkelerin gereksinimleri, özellikle gözönüne alınır."),
                   Clause(clauseNumber:24, clause: "1. Taraf Devletler, çocuğun olabilecek en iyi sağlık düzeyine kavuşma, tıbbi bakım ve rehabilitasyon hizmetlerini veren kuruluşlardan yararlanma hakkını tanırlar. Taraf Devletler, hiçbir çocuğun bu tür tıbbi bakım hizmetlerinden yararlanma hakkından yoksun bırakılmamasını güvence altına almak için çaba gösterirler. 2. Taraf Devletler, bu hakkın tam olarak uygulanmasını takip ederler ve özellikle: a) Bebek ve çocuk ölüm oranlarının düşürülmesi; b) Bütün çocuklara gerekli tıbbi yardımın ve tıbbi bakımın; temel sağlık hizmetlerinin geliştirilmesine önem verilerek sağlanması; c) Temel sağlık hizmetleri çerçevesinde ve başka olanakların yanısıra, kolayca bulunabilen tekniklerin kullanılması ve besleyici yiyecekler ve temiz içme suyu sağlanması yoluyla ve çevre kirlenmesinin tehlike ve zararlarını göz önüne alarak, hastalık ve yetersiz beslenmeye karşı mücadele edilmesi; d) Anneye doğum öncesi ve sonrası uygun bakımın sağlanması; e) Bütün toplum kesimlerinin özellikle ana-babalar ve çocukların, çocuk sağlığı ve beslenmesi, anne sütü ile beslenmenin yararları, toplum ve çevre sağlığı ve kazaların önlenmesi konusunda temel bilgileri elde etmeleri ve bu bilgileri kullanmalarına yardımcı olunması; f) Koruyucu sağlık bakımlarının, ana-babaya rehberliğini, aile planlaması eğitimi ve hizmetlerinin geliştirilmesi; amaçlarıyla uygun önlemleri alırlar. 3. Taraf Devletler, çocukların sağlığı için zararlı geleneksel uygulamaların kaldırılması amacıyla uygun ve etkili her türlü önlemi alırlar. 4. Taraf Devletler, bu maddede tanınan hakkın tam olarak gerçekleştirilmesini tedricen sağlamak amacıyla uluslararası işbirliğinin geliştirilmesi ve teşviki konusunda karşılıklı olarak söz verirler. Bu konuda gelişmekte olan ülkelerin gereksinimleri özellikle göz önünde tutulur."),
                   Clause(clauseNumber:25, clause: "Taraf Devletler, yetkili makamlarca korunma ve bakım altına alma, bedensel ya da ruhsal tedavi amaçlarıyla hakkında bir yerleştirme tedbiri uygulanan çocuğun, gördüğü tedaviyi ve yerleştirilmesine bağlı diğer tüm şartları belli aralıklarla gözden geçirme hakkına sahip olduğunu kabul ederler."),
                   Clause(clauseNumber:26, clause: "1. Taraf Devletler, her çocuğun, sosyal sigorta dahil, sosyal güvenlikten yararlanma hakkını tanır ve bu hakkın tam olarak gerçekleşmesini sağlamak için ulusal hukuklarına uygun, gerekli önlemleri alırlar. 2. Sosyal Güvenlik, çocuğun ve çocuğun bakımından sorumlu olanların kaynakları ve koşulları göz önüne alınarak ve çocuk tarafından ya da onun adına yapılan sosyal güvenlikten yararlanma başvurusuna ilişkin başkaca durumlar da göz önünde tutularak sağlanır."),
                   Clause(clauseNumber:27, clause: "1. Taraf Devletler, her çocuğun bedensel, zihinsel, ruhsal, ahlaksal ve toplumsal gelişmesini sağlayacak yeterli bir hayat seviyesine hakkı olduğunu kabul ederler. 2. Çocuğun gelişmesi için gerekli hayat şartlarının sağlanması sorumluluğu; sahip oldukları imkanlar ve mali güçleri çerçevesinde öncelikle çocuğun ana-babasına veya çocuğun bakımını üstlenen diğer kişilere düşer. 3. Taraf Devletler, ulusal durumlarına göre ve olanakları ölçüsünde, ana-babaya ve çocuğun bakımını üstlenen diğer kişilere, çocuğun bu hakkının uygulanmasında yardımcı olmak amacıyla gerekli önlemleri alır ve gereksinim olduğu takdirde özellikle beslenme, giyim ve barınma konularında maddi yardım ve destek programları uygularlar. 4. Taraf Devletler, Taraf Devlet ülkesinde veya başka ülkede bulunsun; ana-babası veya çocuğa karşı mali sorumluluğu bulunan diğer kişiler tarafından, çocuğun bakım giderlerinin karşılanmasını sağlamak amacıyla her türlü uygun önlemi alırlar. Özellikle çocuğa karşı mali sorumluluğu olan kişinin, çocuğun ülkesinden başka bir ülkede yaşaması halinde, Taraf Devletler bu konuya ilişkin uluslararası anlaşmalara katılmayı veya bu tür anlaşmalar akdinin yanısıra başkaca uygun düzenlemelerin yapılmasını teşvik ederler."),
                   Clause(clauseNumber:28, clause: "1. Taraf Devletler, çocuğun eğitim hakkını kabul ederler ve bu hakkın fırsat eşitliği temeli üzerinde tedricen gerçekleştirilmesi görüşüyle özellikle: a) İlk öğretimi herkes için zorunlu ve parasız hale getirirler; b) Orta öğretim sistemlerinin genel olduğu kadar mesleki nitelikte de olmak üzere çeşitli biçimlerde örgütlenmesini teşvik ederler ve bunların tüm çocuklara açık olmasını sağlarlar ve gerekli durumlarda mali yardım yapılması ve öğretimi parasız kılmak gibi uygun önlemleri alırlar; c) Uygun bütün araçları kullanarak, yüksek öğretimi yetenekleri doğrultusunda herkese açık hale getirirler; d) Eğitim ve meslek seçimine ilişkin bilgi ve rehberliği bütün çocuklar için elde edilir hale getirirler; e) Okullarda düzenli biçimde devamın sağlanması ve okulu terk etme oranlarının düşürülmesi için önlem alırlar. 2. Taraf Devletler, okul disiplininin çocuğun insan olarak taşıdığı saygınlıkla bağdaşır biçimde ve bu Sözleşmeye uygun olarak yürütülmesinin sağlanması amacıyla gerekli olan tüm önlemleri alırlar. 3. Taraf Devletler eğitim alanında, özellikle cehaletin ve okuma yazma bilmemenin dünyadan kaldırılmasına katkıda bulunmak ve çağdaş eğitim yöntemlerine ve bilimsel ve teknik bilgilere sahip olunmasını kolaylaştırmak amacıyla uluslararası işbirliğini güçlendirir ve teşvik ederler. Bu konuda, gelişmekte olan ülkelerin gereksinimleri özellikle göz önünde tutulur."),
                   Clause(clauseNumber:29, clause: "1. Taraf Devletler çocuk eğitiminin aşağıdaki amaçlara yönelik olmasını kabul ederler; a) Çocuğun kişiliğinin, yeteneklerinin, zihinsel ve bedensel yeteneklerinin mümkün olduğunca geliştirilmesi; b) İnsan haklarına ve temel özgürlüklere, Birleşmiş Milletler Andlaşmasında benimsenen ilkelere saygısının geliştirilmesi; c) Çocuğun ana-babasına, kültürel kimliğine, dil ve değerlerine, çocuğun yaşadığı veya geldiği menşe ülkenin ulusal değerlerine ve kendisininkinden farklı uygarlıklara saygısının geliştirilmesi; d) Çocuğun, anlayışı, barış, hoşgörü, cinsler arası eşitlik ve ister etnik, ister ulusal, ister dini gruplardan, isterse yerli halktan olsun, tüm insanlar arasında dostluk ruhuyla, özgür bir toplumda, yaşantıyı, sorumlulukla üstlenecek şekilde hazırlanması; e) Doğal çevreye saygısının geliştirilmesi, 2. Bu maddenin veya 28 inci maddenin hiçbir hükmü gerçek ve tüzel kişilerin öğretim kurumları kurmak ve yönetmek özgürlüğüne, bu maddenin birinci fıkrasında belirtilen ilkelere saygı gösterilmesi ve bu kurumlarda yapılan eğitimin Devlet tarafından konulmuş olan asgari kurallara uygun olması koşuluyla, aykırı sayılacak biçimde yorumlanmayacaktır."),
                   Clause(clauseNumber:30, clause: "Soya, dine ya da dile dayalı azınlıkların ya da yerli halkların varolduğu Devletlerde, böyle bir azınlığa mensup olan ya da yerli halktan olan çocuk, ait olduğu azınlık topluluğunun diğer üyeleri ile birlikte kendi kültüründen yararlanma, kendi dinine inanma ve uygulama ve kendi dilini kullanma hakkından yoksun bırakılamaz."),
                   Clause(clauseNumber:31, clause: "1. Taraf Devletler çocuğun dinlenme, boş zaman değerlendirme, oynama ve yaşına uygun eğlence (etkinliklerinde) bulunma ve kültürel ve sanatsal yaşama serbestçe katılma hakkını tanırlar. 2. Taraf Devletler, çocuğun kültürel ve sanatsal yaşama tam olarak katılma hakkını saygı duyarak tanırlar ve özendirirler ve çocuklar için, boş zamanı değerlendirmeye, dinlenmeye, sanata ve kültüre ilişkin (etkinlikler) konusunda uygun ve eşit fırsatların sağlanmasını teşvik ederler."),
                   Clause(clauseNumber:32, clause: "1. Taraf Devletler, çocuğun, ekonomik sömürüye ve her türlü tehlikeli işte ya da eğitimine zarar verecek ya da sağlığı veya bedensel, zihinsel, ruhsal, ahlaksal ya da toplumsal gelişmesi için zararlı olabilecek nitelikte çalıştırılmasına karşı korunma hakkını kabul ederler. 2. Taraf Devletler, bu maddenin uygulamaya konulmasını sağlamak için yasal, idari, toplumsal ve eğitsel her önlemi alırlar. Bu amaçlar ve öteki uluslararası belgelerin ilgili hükümleri göz önünde tutularak, Taraf Devletler özellikle şu önlemleri alırlar: a) İşe kabul için bir ya da birden çok asgari yaş sınırı tespit ederler; b) Çalışmanın saat olarak süresi ve koşullarına ilişkin uygun düzenlemeleri yaparlar; c) Bu maddenin etkili biçimde uygulanmasını sağlamak için ceza veya başka uygun yaptırımlar öngörürler."),
                   Clause(clauseNumber:33, clause: "Taraf Devletler, çocukların uluslararası anlaşmalarda tanımladığı biçimde uyuşturucu ve psikotrop maddelerin yasadışı kullanımına karşı korunması ve çocukların bu tür maddelerin yasadışı üretimi ve kaçakçılığı alanında kullanılmasını önlemek amacıyla, yasal, sosyal ve eğitsel niteliktekiler de dahil olmak üzere, her türlü uygun önlemleri alırlar."),
                   Clause(clauseNumber:34, clause: "Taraf Devletler, çocuğu her türlü cinsel sömürüye ve cinsel suistimale karşı koruma güvencesi verirler. Bu amaçla Taraf Devletler özellikle: a) Çocuğun yasadışı bir cinsel faaliyete girişmek üzere kandırılması veya zorlanmasını; b) Çocukların, fuhuş, ya da diğer yasadışı cinsel faaliyette bulundurularak sömürülmesini; c) Çocukların pornografik nitelikli gösterilerde ve malzemede kullanılarak sömürülmesini; önlemek amacıyla ulusal düzeyde ve ikili ile çok taraflı ilişkilerde gerekli her türlü önlemi alırlar."),
                   Clause(clauseNumber:35, clause: "Taraf Devletler, her ne nedenle ve hangi biçimde olursa olsun, çocukların kaçırılmaları, satılmaları veya fuhuşa konu olmalarını önlemek için ulusal düzeyde ve ikili ve çok yanlı ilişkilerde gereken her türlü önlemleri alırlar."),
                   Clause(clauseNumber:36, clause: "Taraf Devletler, esenliğine herhangi bir biçimde zarar verebilecek başka her türlü sömürüye karşı çocuğu korurlar."),
                   Clause(clauseNumber:37, clause: "Taraf Devletler aşağıdaki hususları sağlarlar: a) Hiçbir çocuk, işkence veya diğer zalimce, insanlık dışı veya aşağılayıcı muamele ve cezaya tabi tutulmayacaktır. Onsekiz yaşından küçük olanlara, işledikleri suçlar nedeniyle idam cezası verilemeyeceği gibi salıverilme koşulu bulunmayan ömür boyu hapis cezası da verilmeyecektir. b) Hiçbir çocuk yasadışı ya da keyfi biçimde özgürlüğünden yoksun bırakılmayacaktır. Bir çocuğun tutuklanması, alıkonulması veya hapsi yasa gereği olacak ve ancak en son başvurulacak bir önlem olarak düşünülüp, uygun olabilecek en kısa süre ile sınırlı tutulacaktır. c) Özgürlüğünden yoksun bırakılan her çocuğa insancıl biçimde ve insan kişiliğinin özünde bulunan saygınlık ve kendi yaşındaki kişilerin gereksinimleri göz önünde tutularak davranılacaktır. Özgürlüğünden yoksun olan her çocuk, kendi yüksek yararı aksini gerektirmedikçe, özellikle yetişkinlerden ayrı tutulacak ve olağanüstü durumlar dışında ailesi ile yazışma ve görüşme yoluyla ilişki kurma hakkına sahip olacaktır. d) Özgürlüğünden yoksun bırakılan her çocuk, kısa zamanda yasal ve uygun olan diğer yardımlardan yararlanma hakkına sahip olacağı gibi özgürlüğünden yoksun bırakılmasının yasaya aykırılığını bir mahkeme veya diğer yetkili, bağımsız ve tarafsız makam önünde iddia etme ve böylesi bir işlemle ilgili olarak ivedi karar verilmesini isteme hakkına da sahip olacaktır."),
                   Clause(clauseNumber:38, clause: "1. Taraf Devletler, silahlı çatışma halinde kendilerine uygulanabilir olan uluslararası hukukun, çocukları da kapsayan insani kurallarına uymak ve uyulmasını sağlamak yükümlülüğünü üstlenirler. 2. Taraf Devletler, onbeş yaşından küçüklerin çatışmalara doğrudan katılmaması için uygun olan bütün önlemleri alırlar. 3. Taraf Devletler, özellikle onbeş yaşına gelmemiş çocukları askere almaktan kaçınırlar. Taraf Devletler, onbeş ile onsekiz yaş arasındaki çocukların silah altına alınmaları gereken durumlarda, önceliği yaşça büyük olanlara vermek için çaba gösterirler. 4. Silahlı çatışmalarda sivil halkın korunmasına ilişkin uluslararası insani hukuk kuralları tarafından öngörülen yükümlülüklerine uygun olarak, Taraf Devletler, silahlı çatışmadan etkilenen çocuklara koruma ve bakım sağlamak amacıyla mümkün olan her türlü önlemi alırlar."),
                   Clause(clauseNumber:39, clause: "Taraf Devletler, her türlü ihmal, sömürü ya da suistimal, işkence ya da her türlü zalimce, insanlık dışı veya aşağılayıcı muamele ya da ceza uygulaması ya da silahlı çatışma mağduru olan bir çocuğun, bedensel ve ruhsal bakımdan sağlığına yeniden kavuşması ve yeniden toplumla bütünleşebilmesini temin için uygun olan tüm önlemleri alırlar. Bu tür sağlığa kavuşturma ve toplumla bütünleştirme, çocuğun sağlığını, özgüvenini ve saygınlığını geliştirici bir ortamda gerçekleştirilir."),
                   Clause(clauseNumber:40, clause: "1. Taraf Devletler, hakkında ceza yasasını ihlal ettiği iddia edilen ve bu nedenle itham edilen ya da ihlal ettiği kabul edilen her çocuğun; çocuğun yaşı ve yeniden topluma kazandırılmasının ve toplumda yapıcı rol üstlenmesinin arzu edilir olduğu hususları göz önünde bulundurularak, taşıdığı saygınlık ve değer duygusunu geliştirecek ve başkalarının da insan haklarına ve temel özgürlüklerine saygı duymasını pekiştirecek nitelikte muamele görme hakkını kabul ederler. 2. Bu amaçla ve uluslararası belgelerin ilgili hükümleri göz önünde tutularak Taraf Devletler özellikle, şunları sağlarlar: a) İşlendiği zaman ulusal ya da uluslararası hukukça yasaklanmamış bir eylem ya da ihmal nedeniyle hiçbir çocuk hakkında ceza yasasını ihlal ettiği iddiası ya da ithamı öne sürülemeyeceği gibi böyle bir ihlalde bulunduğu da kabul edilmeyecektir. b) Hakkında ceza kanununu ihlal iddiası veya ithamı bulunan her çocuk aşağıdaki asgari güvencelere sahiptir: i) Haklarındaki suçlama yasal olarak sabit oluncaya kadar masum sayılmak; ii) Haklarındaki suçlamalardan kendilerinin hemen ve doğrudan doğruya; ya da uygun düşen durumlarda ana-babaları ya da yasal vasileri kanalı ile haberli kılınmak ve savunmalarının hazırlanıp sunulmasında gerekli yasal ya da uygun olan başka yardımdan yararlanmak; iii) Yetkili, bağımsız ve yansız bir makam ya da mahkeme önünde adli ya da başkaca uygun yardımdan yararlanarak ve özellikle çocuğun yaşı ve durumu göz önüne alınmak suretiyle kendisinin yüksek yararına aykırı olduğu saptanmadığı sürece, ana-babası veya yasal vasisi de hazır bulundurularak yasaya uygun biçimde adil bir duruşma ile konunun gecikmeksizin karara bağlanmasının sağlanması; iv) Tanıklık etmek ya da suç ikrarında bulunmak için zorlanmamak; aleyhine olan tanıkları sorguya çekmek veya sorguya çekmiş olmak ve lehine olan tanıkların hazır bulunmasının ve sorgulanmasının eşit koşullarda sağlanması; v) Ceza yasasını ihlal ettiği sonucuna varılması halinde, bu kararın ve bunun sonucu olarak alınan önlemlerin daha yüksek yetkili, bağımsız ve yansız bir makam ya da mahkeme önünde yasaya uygun olarak incelenmesi; vi) Kullanılan dili anlamaması veya konuşamaması halinde çocuğun parasız çevirmen yardımından yararlanması; vii) Kovuşturmanın her aşamasında özel hayatının gizliliğine tam saygı gösterilmesine hakkı olmak; 3. Taraf Devletler, hakkında ceza yasasını ihlal ettiği iddiası ileri sürülen, bununla itham edilen ya da ihlal ettiği kabul olunan çocuk bakımından, yalnızca ona uygulanabilir yasaların, usullerin, onunla ilgili makam ve kuruluşların oluşturulmasını teşvik edecek ve özellikle şu konularda çaba göstereceklerdir: a) Ceza Yasasını ihlal konusunda asgari bir yaş sınırı belirleyerek, bu yaş sınırının altındaki çocuğun ceza ehliyetinin olmadığının kabulü; b) Uygun bulunduğu ve istenilir olduğu takdirde, insan hakları ve yasal güvencelere tam saygı gösterilmesi koşulu ile bu tür çocuklar için adli kovuşturma olmaksızın önlemlerin alınması. 4. Koruma tedbiri, yönlendirme ve gözetim kararları, danışmanlık, şartlı salıverme, bakım için yerleştirme, eğitim ve meslek öğretme programları ve diğer kurumsal bakım seçenekleri gibi çeşitli düzenlemelerin uygulanmasında, çocuklara durumları ve suçları ile orantılı ve kendi esenliklerine olacak biçimde muamele edilmesi sağlanacaktır."),
                   Clause(clauseNumber:41, clause: "Bu Sözleşmede yer alan hiçbir husus, çocuk haklarının gerçekleştirilmesine daha çok yardımcı olan ve; a) bir Taraf Devletin yasasında; veya b) bu Devlet bakımından yürürlükte oları uluslararası hukukta yer alan hükümleri etkilemeyecektir."),
                   Clause(clauseNumber:42, clause: "Taraf Devletler, Sözleşme ilke ve hükümlerinin uygun ve etkili araçlarla yetişkinler kadar çocuklar tarafından da yaygın biçimde öğrenilmesini sağlamayı taahhüt ederler."),
                   Clause(clauseNumber:43, clause: "1. Taraf Devletlerin bu Sözleşme ile üstlendikleri yükümlülükleri yerine getirme konusunda kaydettikleri ilerlemeleri incelemek amacıyla, görevleri aşağıda belirtilen bir Çocuk Hakları Komitesi kurulmuştur. 2. Komite bu Sözleşme ile hükme bağlanan alanda yetenekleriyle tanınmış ve yüksek ahlak sahibi on uzmandan oluşur. Komite üyeleri Taraf Devletlerce kendi vatandaşları arasından ve kişisel olarak görev yapmak üzere, adil bir coğrafi dağılımı sağlama gereği ve başlıca hukuk sistemleri gözönünde tutularak seçilirler. 3. Komite üyeleri, Taraf devletlerce gösterilen kişiler listesinden gizli oyla seçilirler. Her Taraf Devlet, vatandaşları arasından bir uzmanı aday gösterebilir. 4. Komite için ilk seçim, bu Sözleşmenin yürürlüğe girişini izleyen altı ay içinde yapılır. Sonraki seçimler iki yılda bir yapılır. Her seçim tarihinden en az dört ay önce, Birleşmiş Milletler Teşkilatı Genel Sekreteri, Taraf Devletleri iki ay içinde adaylarını göstermeye yazılı olarak davet eder. Daha sonra Genel Sekreter böylece belirlenen kişilerden, kendilerini gösteren Taraf Devletleri de işaret ederek, alfabetik sıraya göre oluşturduğu bir listeyi, Taraf Devletlere bildirir. 5. Seçimler, Birleşmiş Milletler Teşkilatı Merkezinde, Genel Sekreter tarafından davet edilen Taraf Devletler toplantılarında yapılır. Nisabı, Taraf Devletlerin üçte ikisinin oluşturduğu bu toplantılarda, hazır bulunan ve oy kullanan Devletlerin salt çoğunluğuyla en fazla oy alan kişiler Komiteye seçilir. 6. Komite üyeleri dört yıl için seçilir. Aday gösterildikleri takdirde yeniden seçilebilirler. İlk seçimde seçilmiş olan beş üyenin görevi iki yıl sonra sona erer, bu beş üyenin isimleri ilk seçimden hemen sonra toplantı başkanı tarafından çekilen kura ile belirlenir. 7. Bir Komite üyesinin ölmesi veya çekilmesi ya da başka herhangi bir nedenle bir üyenin Komitedeki görevlerini yapamaz hale gelmesi durumunda adaylığını öneren Taraf Devlet, Komitenin onaylaması koşuluyla, böylece boşalan yerdeki görev süresi doluncaya kadar, kendi vatandaşları arasından başka bir uzmanı atayabilir. 8. Komite, iç tüzüğünü kendisi belirler. 9. Komite, memurlarını iki yıllık bir süre için seçer. 10. Komite toplantıları olağan olarak Birleşmiş Milletler Teşkilatı Merkezinde ya da Komite tarafından belirlenecek başka uygun bir yerde yapılır. Komite olağan olarak her yıl toplanır. Komite toplantılarının süresi, gerektiğinde, Genel Kurulca onaylanmak koşuluyla, bu Sözleşmeye Taraf Devletlerin bir toplantısıyla belirlenir veya değiştirilir. 11. Birleşmiş Milletler Teşkilatı Genel Sekreteri gerekli maddi araçları ve personeli bu Sözleşme ile kendisine verilen görevleri etkili biçimde görebilmesi amacıyla, Komite emrine verir. 12. Bu Sözleşme uyarınca oluşturulan Komitenin üyeleri, Genel Kurulun onayı ile, Birleşmiş Milletler Teşkilatının kaynaklarından karşılanmak üzere, Genel Kurulca saptanan şart ve koşullar çerçevesinde kararlaştırılan ücreti alırlar."),
                   Clause(clauseNumber:44, clause: "1. Taraf Devletler, bu Sözleşmede tanınan hakları yürürlüğe koymak için, aldıkları önlemleri ve bu haklardan yararlanma konusunda gerçekleştirilen ilerlemeye ilişkin raporları: a) Bu Sözleşmenin, ilgili Taraf Devlet bakımından yürürlüğe giriş tarihinden başlayarak iki yıl içinde, b) Daha sonra beş yılda bir, Birleşmiş Milletler Genel Sekreteri aracılığı ile Komiteye sunmayı taahhüt ederler. 2. Bu madde uyarınca hazırlanan raporlarda, bu Sözleşmeye göre üstlenilen sorumlulukların, şayet varsa, yerine getirilmesini etkileyen nedenler ve güçlükler belirtilecektir. Raporlarda ayrıca, ilgili ülkede Sözleşmenin uygulanması hakkında Komiteyi etraflıca aydınlatacak biçimde yeterli bilgi de bulunacaktır. 3. Komiteye etraflı bilgi içeren bir ilk rapor sunmuş olan Taraf Devlet, bu maddenin 1 (b) bendi gereğince sunacağı sonraki raporlarında daha önce verilmiş olan temel bilgileri tekrarlamayacaktır. 4. Komite, Taraf Devletlerden Sözleşmenin uygulamasına ilişkin her türlü ek bilgi isteminde bulunabilir. 5. Komite, iki yılda bir Ekonomik ve Sosyal Konsey aracılığı ile Genel Kurula faaliyetleri hakkında bir rapor sunar. 6. Taraf Devletler kendi raporlarının ülkelerinde geniş biçimde yayımını sağlarlar."),
                   Clause(clauseNumber:45, clause: "Sözleşmenin etkili biçimde uygulanmasını geliştirme ve Sözleşme kapsamına giren alanda uluslararası işbirliğini teşvik etmek amacıyla: a) Uzmanlaşmış kurumlar, UNICEF ve Birleşmiş Milletler Teşkilatının öteki organları, bu Sözleşmenin kendi yetki alanlarına ilişkin olan hükümlerinin uygulamasının incelenmesi sırasında, temsil edilmek hakkına sahiptirler. Komite; uzmanlaşmış kurumları, UNICEF'i ve uygun bulduğu öteki yetkili kuruluşları, kendi yetki alanlarını ilgilendiren konularda uzman olarak görüş vermeye davet edebilir. Komite, uzmanlaşmış kurumları, UNICEF'i ve Birleşmiş Milletler Teşkilatının öteki organlarını kendi faaliyet alanlarına ilişkin kesimlerde Sözleşmenin uygulanması hakkında rapor sunmaya davet edebilir; b) Komite, uygun bulduğu takdirde, Taraf Devletlerce sunulmuş, bir istem içeren ya da teknik danışma veya yardım ihtiyacını belirten her raporu, gerekiyorsa Komitenin bu istek veya ihtiyaca ilişkin tavsiye ve gözlemlerini de ekleyerek, uzmanlaşmış kurumlara, UNICEF’e ve öteki yetkili kuruluşlara gönderir; c) Komite, Genel Kurula Genel Sekreterden Komite adına çocuk haklarına ilişkin sorunlarda incelemeler yaptırması isteğinde bulunulmasını, tavsiye edebilir; d) Komite, bu Sözleşmenin 44 ve 45 inci maddeleri uyarınca alınan bilgilere dayanarak, telkin ve genel nitelikte tavsiyelerde bulunabilir. Bu telkin ve genel nitelikteki tavsiyeler, ilgili olan her Taraf Devlete gönderilir ve şayet varsa Taraf Devletlerin yorumları ile birlikte Genel Kurulun dikkatine sunulur."),
                   Clause(clauseNumber:46, clause: "Bu Sözleşme bütün Devletlerin imzasına açıktır."),
                   Clause(clauseNumber:47, clause: "Bu Sözleşme onaylamaya bağlı tutulmuştur. Onay belgeleri Birleşmiş Milletler Teşkilatı Genel Sekreteri nezdine tevdi edilecektir."),
                   Clause(clauseNumber:48, clause: "Bu Sözleşme bütün Devletlerin katılmasına açık olacaktır. Katılma belgeleri Birleşmiş Milletler Teşkilatı Genel Sekreteri nezdine tevdi edilecektir."),
                   Clause(clauseNumber:49, clause: "1. Bu Sözleşme, yirminci onay ya da katılma belgesinin Birleşmiş Milletler Teşkilatı Genel Sekreteri nezdine tevdi tarihini izleyen otuzuncu gün yürürlüğe girecektir. 2. Yirminci onay ya da katılma belgesinin tevdiinden sonra bu Sözleşmeyi onaylayacak yada ona katılacak Devletlerin her biri için, bu Sözleşme, söz konusu Devletin onay ya da katılma belgesini tevdi tarihinden sonraki otuzuncu gün yürürlüğe girecektir."),
                   Clause(clauseNumber:50, clause: "1. Bu Sözleşmeye Taraf herhangi bir Devlet bir değişiklik önerisinde bulunabilir ve buna ilişkin metni Birleşmiş Milletler Teşkilatı Genel Sekreteri nezdine tevdi edebilir. Genel Sekreter bunun üzerine değişiklik önerisini Taraf Devletlere, önerinin incelenmesi ve oya konulması amacıyla bir Taraf Devletler Konferansı oluşturulmasını isteyip istemediklerini kendisine bildirmeleri kaydıyla, iletir. Böyle bir duyuru tarihini izleyen dört ay içinde Taraf Devletlerin en az üçte biri söz konusu konferansın toplanmasından yana olduklarını ifade ederlerse Genel Sekreter, Birleşmiş Milletler Teşkilatı çerçevesinde bu konferansı düzenler. Konferansta hazır bulunan ve oy kullanan Taraf Devletlerin çoğunluğu tarafından kabul edilen her değişiklik, onay için Birleşmiş Milletler Genel Kuruluna sunulur. 2. Bu Maddenin 1 inci fıkrasında yer alan hükümlere uygun olarak kabul edilen bir değişiklik, Birleşmiş Milletler Genel Kurulunca onaylandığı ve bu sözleşmeye Taraf Devletlerin üçte iki çoğunluğu tarafından kabul edildiği zaman yürürlüğe girer. 3. Bir değişiklik yürürlüğe girdiği zaman, onu kabul eden Taraf Devletler bakımından bağlayıcılık taşır. Öteki Taraf Devletler bu Sözleşme hükümleri ve daha önce kabul ettikleri her değişiklikle bağlı kalırlar."),
                   Clause(clauseNumber:51, clause: "1. Birleşmiş Milletler Teşkilatı Genel Sekreteri, onay ya da katılma anında yapılabilecek çekincelerin metnini alacak ve bütün Devletlere bildirecektir. 2. Bu Sözleşmenin amacı ve konusu ile bağdaşmayan hiçbir çekinceye izin verilmeyecektir. 3. Çekinceler, Birleşmiş Milletler Teşkilatı Genel Sekreterince, geri alınacağına ilişkin bildirimde bulunma yoluyla her zaman geri alınabilir. Bunun üzerine Genel Sekreter, bütün Devletleri haberdar eder. Böyle bir bildirim, Genel Sekreter tarafından alındığı tarihte işlerlik kazanır."),
                   Clause(clauseNumber:52, clause: "Bir Taraf Devlet, bu Sözleşmeyi, Birleşmiş Milletler Teşkilatı Genel Sekreterine vereceği yazılı bildirim yoluyla feshedebilir. Fesih, bildirimin Genel Sekreter tarafından alınması tarihinden bir yıl sonra geçerli olur."),
                   Clause(clauseNumber:53, clause: "Birleşmiş Milletler T eşkilatı Genel Sekreteri, bu Sözleşmenin tevdi makamı olarak belirlenmiştir."),
                   Clause(clauseNumber:54, clause: "İngilizce, Arapça, Çince, İspanyolca, Fransızca ve Rusça metinleri de aynı derecede geçerli olan bu Sözleşmenin özgün metni, Birleşmiş Milletler Teşkilatı Genel Sekreteri nezdine tevdi edilecektir.Hükümetleri tarafından tam yetkili kılınan aşağıda imzaları bulunan Temsilciler, yukarıdaki kuralların ışığında, bu Sözleşmeyi imzalamışlardır."),
                   Clause(clauseNumber:55, clause: "Türkiye Cumhuriyeti Birleşmiş Milletler Çocuk Hakları Sözleşmesi'nin 17, 29 ve 30. maddeleri hükümlerini T.C. Anayasası ve 24 Temmuz 1923 tarihli Lozan Anlaşması hükümlerine ve ruhuna uygun olarak yorumlama hakkını saklı tutmaktadır.”")
        ]
        
        self.design(Label1)
        self.design(scrollView)
        showDetails()
        
    }
    
    func showDetails () {
        
        chechNumber = rowNumber
        
        if Clauses.count>0{
            
            if chechNumber == 0{
                Label1.text = "ÖNSÖZ"
                Label2.text = Clauses[0].clause
            } else if chechNumber == 55 {
                Label1.text = "İhtirazi Kayıt"
                Label2.text = Clauses[55].clause
            } else {
                Label1.text = "Madde " + "\(chechNumber)"
                Label2.text = Clauses[chechNumber].clause
            }
            
        } else {
            
        }
    }

    func design(_ object:AnyObject){
        
        object.layer.cornerRadius = 6
        object.layer.masksToBounds = true
        object.layer.borderWidth = 1
        object.layer.borderColor = UIColor.cyan.cgColor
        
    }
    
    override func didReceiveMemoryWarning() {
        super.didReceiveMemoryWarning()
        // Dispose of any resources that can be recreated.
    }
    

}
